import React, { useState } from 'react';
import './StudentPanel.css';

const EXTENDED_SUBJECTS = [
  'Matematyka',
  'Fizyka',
  'Chemia',
  'Informatyka',
  'Biologia',
  'Historia',
  'Język polski',
  'Język angielski',
  'Wiedza o społeczeństwie',
  'Język Niemiecki',
  'Język hiszpański',
  'Geografia',
];

export default function StudentPanel() {
  const [extendedSubjects, setExtendedSubjects] = useState([
    'Matematyka',
    'Fizyka',
  ]); // przykładowe początkowe rozszerzenia

  const toggleSubject = (subject) => {
    setExtendedSubjects((prev) =>
      prev.includes(subject)
        ? prev.filter((s) => s !== subject)
        : [...prev, subject]
    );
  };

  return (
    <div className="student-panel-wrapper">
      <header className="student-panel-header">
        <h1>Hej Alicja!</h1>
      </header>

      <section className="profile-section">
        <img
          src="https://via.placeholder.com/100"
          alt="Profilowe"
          className="profile-pic"
        />
        <div className="profile-info">
          <p><strong>Klasa:</strong> 3B</p>
          <p><strong>Przedmioty rozszerzone:</strong></p>
          <ul>
            {extendedSubjects.map((subj) => (
              <li key={subj}>{subj}</li>
            ))}
          </ul>
          <p><strong>Punkty za pomoc:</strong> 120</p>
        </div>
      </section>

      <section className="edit-subjects-section">
        <h2>Edytuj przedmioty rozszerzone</h2>
        <form>
          {EXTENDED_SUBJECTS.map((subject) => (
            <label key={subject} className="subject-checkbox">
              <input
                type="checkbox"
                checked={extendedSubjects.includes(subject)}
                onChange={() => toggleSubject(subject)}
              />
              {subject}
            </label>
          ))}
        </form>
      </section>
    </div>
  );
}
