import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LevelDropdown from './components/LevelDropdown';
import ChatroomList from './components/ChatroomList';
import Chatroom from './components/Chatroom';
import StudentPanel from './components/StudentPanel'; // Twój panel ucznia
import BurgerMenu from './components/BurgerMenu';     // NOWE: burger menu

export default function App() {
  const location = useLocation();
  const [level, setLevel] = useState('Podstawa');
  const [selectedRoom, setSelectedRoom] = useState(null);
  const hideDropdown = location.pathname === '/student-panel';

  return (
    <Router>
      <LevelDropdown level={level} setLevel={setLevel} />
      <BurgerMenu />  {/* Burger zawsze widoczny */}

      <Routes>
        <Route path="/" element={
          selectedRoom ? (
            <Chatroom 
              room={{ subject: selectedRoom, level }}
              goBack={() => setSelectedRoom(null)}
            />
          ) : (
            <ChatroomList level={level} setSelectedRoom={setSelectedRoom} />
          )
        } />
        <Route path="/student-panel" element={<StudentPanel />} />
      </Routes>
    </Router>
  );
}
