import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Register.css';

const allSubjects = [
  'Matematyka', 'Fizyka', 'Chemia', 'Informatyka', 'Biologia',
  'Historia', 'JÄ™zyk polski', 'JÄ™zyk angielski', 'Wiedza o spoÅ‚eczeÅ„stwie',
  'JÄ™zyk Niemiecki', 'JÄ™zyk hiszpaÅ„ski', 'Geografia',
];

export default function Register() {
  const [username, setUsername] = useState('');
  const [selectedSubjects, setSelectedSubjects] = useState([]);
  const [selectedClass, setSelectedClass] = useState('1');
  const { setUser } = useAuth();
  const navigate = useNavigate();

  const toggleSubject = (subject) => {
    if (selectedSubjects.includes(subject)) {
      setSelectedSubjects(selectedSubjects.filter((s) => s !== subject));
    } else {
      setSelectedSubjects([...selectedSubjects, subject]);
    }
  };

  const handleRegister = (e) => {
    e.preventDefault();
    if (username.trim() === '' || selectedSubjects.length === 0) {
      alert('UzupeÅ‚nij nazwÄ™ i wybierz przynajmniej jeden przedmiot.');
      return;
    }

    // ðŸš¨ ZWRÃ“Ä† UWAGÄ˜: uÅ¼yj dokÅ‚adnie tych samych kluczy co w StudentPanel
    setUser({
      name: username,
      className: selectedClass,
      extendedSubjects: selectedSubjects,
    });

    navigate('/student-panel');
  };

  return (
    <div className="register-wrapper">
      <h2>Rejestracja</h2>

      <form onSubmit={handleRegister}>
        <label>Nazwa uÅ¼ytkownika:</label>
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Wpisz swojÄ… nazwÄ™"
        />

        <label>Klasa:</label>
        <select value={selectedClass} onChange={(e) => setSelectedClass(e.target.value)}>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
          <option value="4">4</option>
        </select>

        <label>
          Wybierz przedmiot rozszerzony{' '}
          <span className="flare">bÄ™dziesz mentorem</span>
        </label>

        <div className="checkbox-list">
          {allSubjects.map((subject) => (
            <label key={subject}>
              <input
                type="checkbox"
                checked={selectedSubjects.includes(subject)}
                onChange={() => toggleSubject(subject)}
              />
              {subject}
            </label>
          ))}
        </div>

        <button type="submit">Zarejestruj siÄ™</button>

        <p style={{ textAlign: 'center', marginTop: '1rem' }}>
          Masz juÅ¼ konto? <Link to="/login">Zaloguj siÄ™</Link>
        </p>
      </form>
    </div>
  );
}
