import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000');

export default function Chatroom({ room, goBack }) {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const { subject, level } = room;
  const roomName = `${subject}_${level}`;

  useEffect(() => {

    setMessages([]);
    socket.emit('joinRoom', roomName);



    socket.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.off('message');
    };
  }, [roomName]);

  const sendMessage = () => {
    socket.emit('message', { room: roomName, message });
    setMessage('');
  };

  return (
    <div>
      <h2>Pokój: {subject} ({level})</h2>
      <button onClick={goBack}>⬅ Wróć do listy pokoi</button>
      <div style={{ border: '1px solid black', height: '200px', overflowY: 'scroll' }}>
        {messages.map((msg, idx) => (
          <div key={idx}>{msg}</div>
        ))}
      </div>
      <input
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Napisz wiadomość"
      />
      <button onClick={sendMessage}>Wyślij</button>
    </div>
  );
}
