import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import './Chatroom.css';
const ROOM_COLORS = {
  Matematyka_Podstawa: '#7e96a0',
  Matematyka_Rozszerzenie: '#8a9a5b',
  Fizyka_Podstawa: '#a07e96',
  Fizyka_Rozszerzenie: '#965b8a',
  Chemia_Podstawa: '#7a96a0',
  Chemia_Rozszerzenie: '#9a7e6b',
  // Dodaj inne pokoje i kolory, które chcesz
};


const socket = io('http://localhost:4000');

export default function Chatroom({ room, goBack }) {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const { subject, level } = room;
  const roomName = `${subject}_${level}`;
  const roomColor = ROOM_COLORS[roomName] || '#7e96a0'; // kolor domyślny, jeśli pokoju nie ma w słowniku


  useEffect(() => {
    setMessages([]);
    socket.emit('joinRoom', roomName);

    socket.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.off('message');
    };
  }, [roomName]);

  const sendMessage = () => {
    if (message.trim() === '') return;
    socket.emit('message', { room: roomName, message });
    setMessage('');
  };

  return (
    <div className="chatroom-wrapper">
      <button className="back-button" onClick={goBack}>⬅ Wróć do listy pokoi</button>
      <h2>Pokój: {subject} ({level})</h2>

      <div className="messages">
        {messages.map((msg, idx) => (
          <div 
          key={idx} className="message-bubble">
            {msg}
          </div>
        ))}
      </div>

      <div className="input-area">
        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Napisz wiadomość..."
        />
        <button onClick={sendMessage}>Wyślij</button>
      </div>
    </div>
  );
}
