import React, { useState } from 'react';
import './Login.css';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext'; // <-- import kontekstu

export default function Login() {
  const navigate = useNavigate();
  const { setUser } = useAuth(); // <-- hook z AuthContext

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();

    if (!username.trim() || !password.trim()) {
      setError('Uzupełnij wszystkie pola.');
      return;
    }

    // Symulowane "logowanie"
    // Tu możemy ustawić usera z podstawowymi danymi
    setUser({
      name: username,
      className: '3', // przykładowa klasa — możesz dostosować lub pobrać z backendu
      extendedSubjects: ['Matematyka'], // przykładowe przedmioty
    });

    navigate('/student-panel');
  };

  return (
    <div className="login-wrapper">
      <h2>Zaloguj się</h2>

      <form onSubmit={handleLogin}>
        <label htmlFor="username">Nazwa użytkownika</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Wpisz nazwę użytkownika"
        />

        <label htmlFor="password">Hasło</label>
        <input
          id="password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Wpisz hasło"
        />

        {error && <div className="error-message">{error}</div>}

        <button type="submit">Zaloguj się</button>
        <p style={{ textAlign: 'center', marginTop: '1rem' }}>
          Nie masz konta? <Link to="/register">Zarejestruj się</Link>
        </p>
      </form>
    </div>
  );
}
