import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';
import './Chatroom.css';

export default function Chatroom({ room, goBack }) {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const { subject, level } = room;
  const roomName = `${subject}_${level}`;

  const socketRef = useRef(null);

  // Init socket tylko raz
  useEffect(() => {
    socketRef.current = io('https://peerx-chat.onrender.com');

    // Listener wiadomości
    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socketRef.current.disconnect();
      socketRef.current = null;
    };
  }, []);

  // Przy każdej zmianie pokoju dołącz do nowego i wyczyść wiadomości
  useEffect(() => {
    if (!socketRef.current) return;

    setMessages([]); // czyścimy wiadomości

    socketRef.current.emit('joinRoom', roomName);
  }, [roomName]);

  const sendMessage = () => {
    if (message.trim() === '') return;

    socketRef.current.emit('message', { room: roomName, message });
    setMessage('');
  };

  return (
    <div className="chatroom-wrapper">
      <button className="back-button" onClick={goBack}>⬅ Wróć do listy pokoi</button>
      <h2 style={{ textAlign: 'left', fontSize: '1.2rem', marginBottom: '1rem' }}>
        Pokój: {subject} ({level})
      </h2>

      <div className="messages" style={{ border: '2px solid #ccc', borderRadius: '10px', padding: '15px', height: '400px', overflowY: 'auto', background: '#fafafa' }}>
        {messages.map((msg, idx) => {
          const messageText = typeof msg === 'string' ? msg : msg.message || '';
          const sender = typeof msg === 'string' ? 'other' : msg.sender || 'other';

          return (
            <div
              key={idx}
              className={`message-bubble ${sender === 'me' ? 'my-message' : 'other-message'}`}
            >
              {messageText}
            </div>
          );
        })}
      </div>

      <form
        className="input-area"
        onSubmit={(e) => {
          e.preventDefault();
          sendMessage();
        }}
        style={{ marginTop: '15px', display: 'flex' }}
      >
        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Napisz wiadomość..."
          style={{ flex: 1, padding: '10px 15px', borderRadius: '20px', border: '1px solid #ccc' }}
        />
        <button type="submit" style={{ marginLeft: '10px', padding: '10px 20px', borderRadius: '20px', border: 'none', background: '#007bff', color: '#fff', cursor: 'pointer' }}>
          Wyślij
        </button>
      </form>
    </div>
  );
}
