import React, { useState } from 'react';
import './StudentPanel.css';


const defaultProfilePic = 'https://www.facebook.com/images/fb_icon_325x325.png'; // link do domyślnego avataru

const allSubjects = [
  'Matematyka',
  'Fizyka',
  'Chemia',
  'Informatyka',
  'Biologia',
  'Historia',
  'Język polski',
  'Język angielski',
  'Wiedza o społeczeństwie',
  'Język Niemiecki',
  'Język hiszpański',
  'Geografia',
];

export default function StudentPanel({ selectedExtendedSubjects, setSelectedExtendedSubjects }) {
  const [editing, setEditing] = React.useState(false);
  const [tempSelected, setTempSelected] = React.useState(selectedExtendedSubjects);

  const toggleSubject = (subject) => {
    if (tempSelected.includes(subject)) {
      setTempSelected(tempSelected.filter((s) => s !== subject));
    } else {
      setTempSelected([...tempSelected, subject]);
    }
  };

  const saveChanges = () => {
    setSelectedExtendedSubjects(tempSelected);
    setEditing(false);
  };

  // reszta jak poprzednio, ale używaj tempSelected podczas edycji,
  // a po save zapisujesz do globalnego stanu


  return (
    <div className="student-panel-wrapper">
      {/* Pasek na górze */}
      <header className="student-panel-header">
        <h1>Hej, Alicja!</h1>
      </header>

      <div className="profile-section">
        <img
          src={defaultProfilePic}
          alt="Profilowe"
          className="profile-pic"
        />
        <div className="profile-data">
          <p><strong>Klasa:</strong> {className}</p>

          <div className="subjects-section">
            <div className="subjects-header">
              <h2>Przedmioty rozszerzone</h2>
              {!editing && (
                <button onClick={() => setEditing(true)} className="edit-button">
                  Edytuj
                </button>
              )}
            </div>

            {editing ? (
              <>
                <div className="checkbox-list">
                  {allSubjects.map((subject) => (
                    <label key={subject}>
                      <input
                        type="checkbox"
                        checked={selectedSubjects.includes(subject)}
                        onChange={() => toggleSubject(subject)}
                      />
                      {subject}
                    </label>
                  ))}
                </div>
                <button onClick={saveChanges} className="save-button">Zapisz</button>
              </>
            ) : (
              <ul>
                {selectedSubjects.map((subject) => (
                  <li key={subject}>{subject}</li>
                ))}
              </ul>
            )}
          </div>

          <p><strong>Punkty za pomoc:</strong> {points}</p>
        </div>
      </div>
    </div>
  );
}
