import React from 'react';
import './StudentPanel.css';

export default function StudentPanel() {
  // Na razie dane na sztywno — docelowo możesz je pobierać z backendu
  const user = {
    name: 'Alicja',
    klasa: '3B',
    rozszerzenia: ['Matematyka', 'Fizyka', 'Informatyka'],
    punkty: 120,
    profilowe: 'https://via.placeholder.com/150' // placeholder
  };

  return (
    <div className="student-panel">
      <h2 className="greeting">Hej {user.name}!</h2>

      <div className="profile-section">
        <img src={user.profilowe} alt="Profilowe" className="profile-picture" />

        <div className="user-data">
          <p><strong>Klasa:</strong> {user.klasa}</p>
          <p><strong>Przedmioty rozszerzone:</strong></p>
          <ul>
            {user.rozszerzenia.map((przedmiot, idx) => (
              <li key={idx}>{przedmiot}</li>
            ))}
          </ul>
          <p><strong>Zdobyte punkty za pomoc:</strong> {user.punkty}</p>
        </div>
      </div>
    </div>
  );
}
